function quicksort(arr) {
  // Base case: If the length of the array is 1 or less, return the array (since it is already sorted)
  if (arr.length <= 1) return arr;

  // Choose a pivot element from the array
  let pivot = arr[0];

  // Partition the array into two subarrays: elements less than the pivot and elements greater than the pivot
  let less = [];
  let greater = [];
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] < pivot) {
      less.push(arr[i]);
    } else {
      greater.push(arr[i]);
    }
  }

  // Recursively sort the two subarrays
  less = quicksort(less);
  greater = quicksort(greater);

  // Combine the sorted subarrays and the pivot element
  return less.concat(pivot).concat(greater);
}

